    static int contador = 0;

    public void mostrarBl() {
        
        int speed = 2;

        Timer timer;
        TimerTask tarea;

        int speed1 = speed * 1000;

        tarea = new TimerTask() {
            @Override
            public void run() {
                
                Icon icono, icono2, icono3, icono4, icono5;
                
                switch (contador) {
                    case 0: {
                        contador = 1;
                        icono = new ImageIcon(getClass().getResource("/ImageBiblia/1.png"));
                        JLImagenBL.setIcon(icono);
                        
                        icono2 = new ImageIcon(getClass().getResource("/ImageBiblia/p2.png"));
                        JLPecadoBiblia.setIcon(icono2);
                        
                        icono3 = new ImageIcon(getClass().getResource("/ImageBiblia/c4.png"));
                        JLBreakingChain1.setIcon(icono3);
                        
                        icono4 = new ImageIcon(getClass().getResource("/ImageBiblia/n1.png"));
                        JLRegeneracionBl.setIcon(icono4);
                        
                        icono5 = new ImageIcon(getClass().getResource("/ImageBiblia/prm1.png"));
                        JLPermanecerBl.setIcon(icono5);
                        break;
                    }
                    case 1: {
                        contador = 2;
                        icono = new ImageIcon(getClass().getResource("/ImageBiblia/2.png"));
                        JLImagenBL.setIcon(icono);
                        
                        icono2 = new ImageIcon(getClass().getResource("/ImageBiblia/p3.png"));
                        JLPecadoBiblia.setIcon(icono2);
                        
                        icono3 = new ImageIcon(getClass().getResource("/ImageBiblia/c2.png"));
                        JLBreakingChain1.setIcon(icono3);
                        
                        icono4 = new ImageIcon(getClass().getResource("/ImageBiblia/n2.png"));
                        JLRegeneracionBl.setIcon(icono4);
                        
                        icono5 = new ImageIcon(getClass().getResource("/ImageBiblia/prm2.png"));
                        JLPermanecerBl.setIcon(icono5);
                        break;
                    }
                    case 2: {
                        contador = 3;
                        icono = new ImageIcon(getClass().getResource("/ImageBiblia/3.png"));
                        JLImagenBL.setIcon(icono);
                        
                        icono2 = new ImageIcon(getClass().getResource("/ImageBiblia/p4.png"));
                        JLPecadoBiblia.setIcon(icono2);
                        
                        icono3 = new ImageIcon(getClass().getResource("/ImageBiblia/c3.png"));
                        JLBreakingChain1.setIcon(icono3);
                        
                        icono4 = new ImageIcon(getClass().getResource("/ImageBiblia/n6.png"));
                        JLRegeneracionBl.setIcon(icono4);
                        
                        icono5 = new ImageIcon(getClass().getResource("/ImageBiblia/prm3.png"));
                        JLPermanecerBl.setIcon(icono5);
                        break;
                    }
                    case 3: {
                        contador = 4;
                        icono = new ImageIcon(getClass().getResource("/ImageBiblia/4.png"));
                        JLImagenBL.setIcon(icono);
                        
                        icono2 = new ImageIcon(getClass().getResource("/ImageBiblia/p5.png"));
                        JLPecadoBiblia.setIcon(icono2);
                        
                        icono3 = new ImageIcon(getClass().getResource("/ImageBiblia/c5.png"));
                        JLBreakingChain1.setIcon(icono3);
                        
                        icono4 = new ImageIcon(getClass().getResource("/ImageBiblia/n3.png"));
                        JLRegeneracionBl.setIcon(icono4);
                        
                        icono5 = new ImageIcon(getClass().getResource("/ImageBiblia/prm4.png"));
                        JLPermanecerBl.setIcon(icono5);
                        break;
                    }
                    case 4: {
                        contador = 0;
                        icono = new ImageIcon(getClass().getResource("/ImageBiblia/5.png"));
                        JLImagenBL.setIcon(icono);
                        
                        icono2 = new ImageIcon(getClass().getResource("/ImageBiblia/p6.png"));
                        JLPecadoBiblia.setIcon(icono2);
                        
                        icono3 = new ImageIcon(getClass().getResource("/ImageBiblia/c1.png"));
                        JLBreakingChain1.setIcon(icono3);
                        
                        icono4 = new ImageIcon(getClass().getResource("/ImageBiblia/n5.png"));
                        JLRegeneracionBl.setIcon(icono4);
                        
                        icono5 = new ImageIcon(getClass().getResource("/ImageBiblia/prm5.png"));
                        JLPermanecerBl.setIcon(icono5);
                        break;
                    }
                }
            }
        };

        timer = new Timer();
        timer.scheduleAtFixedRate(tarea, speed1, speed1);
    }
    
